query:
mutation acknowledgeUnsafeGasReading($formId: ID!, $entryId: ID!) {
  acknowledgeUnsafeGasReading(formId: $formId, entryId: $entryId) {
    crewId
    gasReadings {
      gasName
      threshold
      reading
      unit
      __typename
    }
    entryStatus
    gasReadingStatus
    __typename
  }
}
