query:
fragment riskMethodFragment on RiskHazardsWithMethod {
  __typename
  methodDescription
  hazards {
    __typename
    identifiedHazard
    existingMeasures
    additionalMeasures
    likelihood
    consequence
    likelihoodAfterExistingMeasures
    consequenceAfterExistingMeasures
    likelihoodAfterAdditionalMeasures
    consequenceAfterAdditionalMeasures
  }
}

fragment signatureWithLocationFragment on SignatureWithLocation {
  signed {
    ...userIdWithRank
    __typename
  }
  signedAt
  signedOn {
    ...dateTimeWithOffsetFragment
    __typename
  }
  signatureString
  __typename
}

fragment crewMemberFragment on CrewMember {
  __typename
  _id
  rank
  firstName
  lastName
}

fragment dateTimeWithOffsetFragment on DateTimeWithOffset {
  __typename
  dateTime
  utcOffset
}

fragment userIdWithRank on UserIdWithRank {
  userId
  rank
  crewMember {
    ...crewMemberFragment
    __typename
  }
  __typename
}

fragment locationFragment on LocationValue {
  zone
  zoneDetails
  __typename
}

fragment rolesAndResponsibilitiesFragment on RoleAndResponsibility {
  role
  crewId
  signature
  signedOn {
    dateTime
    utcOffset
    __typename
  }
  signedAt
  signed {
    ...userIdWithRank
    __typename
  }
  otherCompany
  otherName
  __typename
}

fragment gasReaderEntryRecordFragment on GasReaderEntryRecord {
  crewId
  gasReadingEquipment {
    gasEquipment
    gasSrNumber
    gasLastCalibrationDate
    __typename
  }
  gasReadings {
    gasName
    threshold
    reading
    unit
    __typename
  }
  gasReadingTime {
    dateTime
    utcOffset
    __typename
  }
  gasReadingStatus
  timeIn {
    dateTime
    utcOffset
    __typename
  }
  timeOut {
    dateTime
    utcOffset
    __typename
  }
  signature
  signed {
    ...userIdWithRank
    __typename
  }
  locationId
  __typename
}

fragment formAnswersFragment on FormAnswersSegmentedByType {
  stringAnswers {
    name
    answer {
      __typename
      value
    }
    __typename
  }
  floatAnswers {
    name
    answer {
      __typename
      value
    }
    __typename
  }
  dateTimeAnswers {
    name
    answer {
      __typename
      value {
        ...dateTimeWithOffsetFragment
        __typename
      }
    }
    __typename
  }
  listOfCrewAnswers {
    name
    answer {
      __typename
      value {
        ...userIdWithRank
        __typename
      }
    }
    __typename
  }
  listOfStringsAnswers {
    name
    answer {
      __typename
      value
    }
    __typename
  }
  riskHazardsAnswers {
    name
    answer {
      __typename
      value {
        ...riskMethodFragment
        __typename
      }
    }
    __typename
  }
  locationAnswers {
    name
    answer {
      __typename
      value {
        ...locationFragment
        __typename
      }
    }
    __typename
  }
  listOfGasReadersAnswers {
    name
    answer {
      __typename
      value {
        ...gasReaderEntryRecordFragment
        __typename
      }
    }
    __typename
  }
  signatureWithLocationAnswers {
    name
    answer {
      __typename
      value {
        ...signatureWithLocationFragment
        __typename
      }
    }
    __typename
  }
  rolesAndResponsibilitiesAnswers {
    name
    answer {
      __typename
      value {
        ...rolesAndResponsibilitiesFragment
        __typename
      }
    }
    __typename
  }
  __typename
}

fragment entryFormFragment on Form {
  _id
  answers {
    ...formAnswersFragment
    __typename
  }
  created {
    user {
      _id
      crewMember {
        rank
        firstName
        lastName
        __typename
      }
      __typename
    }
    __typename
  }
  formType
  lastUpdated
  status
  __typename
}

query ListFormsStatus($types: [FormType!], $statuses: [FormStatusType!]) {
  forms(types: $types, statuses: $statuses) {
    edges {
      node {
        ...entryFormFragment
        __typename
      }
      __typename
    }
    __typename
  }
}
