query:
fragment formStatsFragment on FormStats {
  __typename
  active
  pendingOfficerApproval
  pendingMasterReview
  pendingOfficeApproval
  pendingMasterApproval
  pendingTermination
  approvalUpdatesNeeded
  activeUpdatesNeeded
  terminationUpdatesNeeded
