query:
fragment dateTimeWithOffsetFragment on DateTimeWithOffset {
  dateTime
  utcOffset
  __typename
}

mutation createPREDEntryRecord($formId: ID!, $crewId: ID!, $signature: String!, $locationId: ID, $gasReadings: [GasReadingInput!]!, $otherEntrantIds: [ID!]!, $purposeOfEntry: String!) {
  createPREDEntryRecord(formId: $formId, crewId: $crewId, signature: $signature, locationId: $locationId, gasReadings: $gasReadings, otherEntrantIds: $otherEntrantIds, purposeOfEntry: $purposeOfEntry) {
    crewId
    entryStatus
    entryId
    entrant {
      _id
      rank
      firstName
      lastName
      __typename
    }
    purposeOfEntry
    timeIn {
      ...dateTimeWithOffsetFragment
      __typename
    }
    autoSignIn
    timeOut {
      ...dateTimeWithOffsetFragment
      __typename
    }
    autoSignOut
    validityStart {
      ...dateTimeWithOffsetFragment
      __typename
    }
    validityEnd {
      ...dateTimeWithOffsetFragment
      __typename
    }
    gasReadingTime {
      ...dateTimeWithOffsetFragment
      __typename
    }
    gasReadingStatus
    signature
    locationId
    gasReadings {
      gasName
      threshold
      reading
      unit
      __typename
    }
    __typename
  }
}
