query forms($types: [FormType!], $statuses: [FormStatusType!], $first: Int, $descending: Boolean, $after: String) {
  forms(types: $types, statuses: $statuses, first: $first, descending: $descending, after: $after) {
    edges {
      node {
        ...formFragment
        __typename
      }
      cursor
      __typename
    }
    pageInfo {
      endCursor
      hasNextPage
      __typename
    }
    __typename
  }
}
