query:
mutation UpdateFormAnswers($formId: ID!, $parentFormId: ID, $sectionId: String!, $submissionTimestamp: DateTime, $answers: [SerializedFormAnswerInput!]!) {
  updateFormAnswers(formId: $formId, parentFormId: $parentFormId, sectionId: $sectionId, submissionTimestamp: $submissionTimestamp, answers: $answers) {
    _id
    __typename
  }
}
